package com.neuedu.day01;

public class Calculate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 435234+23523
		int x = 314123;
		// 变量类型 变量名=值
		int y = 23;
		int z = x + y;
		System.out.println("计算结果为："+z);
		
		float x1=0.12f;//赋值时后面加f
		float x2=2.4F;
		float x3=x1*x2;
		
		System.out.println("计算结果为："+x3);
		
		char q1='你';
		char q2='7';
		System.out.println(q2);
		
		boolean w1=true;
		boolean w2=false;
		System.out.println(w1);
		short p3=32767;
		int p5=400000000;
		int p4=p3*p5;
		System.out.println(p4);
		
		byte r1=-128;
		System.out.println("我自己写的代码");
		System.out.println("好吧 我pull之后更新了别人的代码");
		
		System.out.println("别人修改了，你直接commit会有冲突");
		
		System.out.println("二二二二二次次次次次捏人别人别人");
	}

}
